import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import config from '../config';
import './ForumPage.css';

const ForumPage = () => {
  const [categories, setCategories] = useState([]);
  const [editingCategory, setEditingCategory] = useState(null);
  const [editManufacturer, setEditManufacturer] = useState('');
  const [newCategoryManufacturer, setNewCategoryManufacturer] = useState('');
  const [newCategoryServerType, setNewCategoryServerType] = useState('');
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const { accessToken, waitForAccessToken, loading, user } = useAuth();  // user is fetched from context
  const navigate = useNavigate();

  useEffect(() => {
    // Log the entire user object
    console.log("User Info:", user); // This will log all the information about the user

    const fetchCategories = async () => {
      try {
        const token = await waitForAccessToken();
        const response = await axios.get(`${config.apiBaseUrl}/api/category`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();
  }, [waitForAccessToken, user]);  // Including user as a dependency to log it when the user changes

  const handleDelete = async (categoryId) => {
    try {
      const token = await waitForAccessToken();
      await axios.delete(`${config.apiBaseUrl}/api/category/${categoryId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCategories(categories.filter((category) => category.id !== categoryId));
    } catch (error) {
      if (error.response?.status === 403) {
        alert('Unauthorized access');
      } else {
        console.error('Error deleting category:', error);
      }
    }
  };

  const handleEditSave = async () => {
    try {
      const token = await waitForAccessToken();
      await axios.put(
        `${config.apiBaseUrl}/api/category/${editingCategory.id}`,
        { manifacturer: editManufacturer },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCategories(categories.map((category) =>
        category.id === editingCategory.id
          ? { ...category, manifacturer: editManufacturer }
          : category
      ));
      closeEditModal();
    } catch (error) {
      if (error.response?.status === 403) {
        alert('Unauthorized access');
      } else {
        console.error('Error saving edits:', error);
      }
    }
  };

  const openEditModal = (category) => {
    setEditingCategory(category);
    setEditManufacturer(category.manifacturer);
  };

  const closeEditModal = () => {
    setEditingCategory(null);
  };

  const openCreateModal = () => {
    setNewCategoryManufacturer('');
    setNewCategoryServerType('');
    setIsCreateModalOpen(true);
  };

  const closeCreateModal = () => {
    setIsCreateModalOpen(false);
  };

  const handleCreateSave = async () => {
    try {
      const token = await waitForAccessToken();
      const response = await axios.post(
        `${config.apiBaseUrl}/api/category`,
        {
          manifacturer: newCategoryManufacturer,
          serverType: newCategoryServerType,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCategories([response.data, ...categories]);
      closeCreateModal();
    } catch (error) {
      console.error('Error creating category:', error);
    }
  };

  if (loading) return <p>Loading...</p>;

  // Ensure that user is defined and has the roles property
  const isAdmin = user?.roles?.includes('Admin');
  const isShopUser = user?.roles?.includes('ShopUser');

  return (
    <div className="category-page">
      <h2>Categories</h2>

      {/* Show Create button only for Admin */}
      {isAdmin && (
        <div className="create-new-button">
          <button className="edit-button" onClick={openCreateModal}>Create New Category</button>
        </div>
      )}

      <div>
        {categories.map((category) => (
          <div key={category.id} className="category-item">
            <div className="category-header">
              <h3>{category.manifacturer}</h3>
              <p>Server Type: {category.serverType}</p>
            </div>
            {/* Show Edit and Delete buttons only for Admin */}
            {isAdmin && (
              <div className="category-actions">
                <button className="edit-button" onClick={() => openEditModal(category)}>Edit</button>
                <button className="delete-button" onClick={() => handleDelete(category.id)}>Delete</button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Edit Category Modal */}
      {editingCategory && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Category</h2>
            <label>Manufacturer</label>
            <input
              type="text"
              value={editManufacturer}
              onChange={(e) => setEditManufacturer(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleEditSave}>Save</button>
              <button onClick={closeEditModal}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Create Category Modal */}
      {isCreateModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Create New Category</h2>
            <label>Manufacturer</label>
            <input
              type="text"
              value={newCategoryManufacturer}
              onChange={(e) => setNewCategoryManufacturer(e.target.value)}
            />
            <label>Server Type</label>
            <input
              type="text"
              value={newCategoryServerType}
              onChange={(e) => setNewCategoryServerType(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleCreateSave}>Create</button>
              <button onClick={closeCreateModal}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Display message if user is not authorized */}
      {(!isAdmin && !isShopUser) && (
        <p>You do not have access to view this page.</p>
      )}
    </div>
  );
};

export default ForumPage;
